sidebar-center #pagelines_content #sidebar-wrap{ width:21.818181818182%; }";s:8:"sections";s:22890:".section-postauthor{\n	margin-bottom: 2em;\n	text-align: left;\n	.copy-pad {\n		padding:  10px 15px;\n	}\n	.author-note{\n		.noteText();\n	}\n	.subtext{\n		font-size: 86%;\n		color: @textColorAlt;\n	}\n}\n.section-postauthor{\n	margin-bottom: 2em;\n	text-align: left;\n	.copy-pad {\n		padding:  10px 15px;\n	}\n	.author-note{\n		.noteText();\n	}\n	.subtext{\n		font-size: 86%;\n		color: @textColorAlt;\n	}\n}\n.section-simple_nav .pl-section-pad{\n	padding: 15px 0;\n}\n.simplenav {\n	text-align: center;\n	&.align-left{\n		text-align: left;\n	}\n	&.align-right{\n		text-align: right;\n	}\n	li a{\n		font-size: @smallerText;\n		color: inherit;\n		&:hover{\n			opacity: .7;\n		}\n		display: inline-block;\n		margin: 0 5px;\n		padding: 2px 4px;\n	}\n}\n.section-secondnav{\n	&.container{\n		margin: 0;\n	}\n	ul{\n		list-style:none;\n		margin:0;\n		li{\n			float: left;\n			a{\n				display: block;\n				padding: 3px 8px;\n				border:none;\n				&:active{\n					text-decoration:none;\n				}\n			}\n		}\n	}\n	.secondnav_menu {\n		margin:0 0 0 0px;\n	}\n}\n.pl-sharebar-pad{\n	line-height: 20px;\n	padding: 10px;\n	text-align: center;\n}\n.pl-sharebar-text{\n	.noteText();\n}\n\n.meta-share{\n	padding-top: 5px;\n	iframe, object{\n		width: auto;\n	}\n}\n\n.fb-like{\n	margin-right: 20px;\n}\n\n.pin_wrap {\n	display: inline-block;\n}/*\n * PageLines Inc.\n */\n\nbody.navbar_fixed {\n	.page-canvas {\n		padding-top: 50px;\n	}\n	.fixed_width #page .page-canvas{\n		margin-top: 42px;\n		padding-top: 0;\n	}\n\n	.section-navbar.fixed-top{\n		position: fixed;\n		z-index: @zindexFixedNavbar;\n	}\n	\n}\n\n.section-navbar.fixed-top{\n	.navbar-edit{\n		position: absolute;\n		left: -30px;\n		top: 6px;\n		display: none;\n	}\n	&:hover{\n		.navbar-edit{\n			display: block;\n		}\n	}\n}\n\n/*  Basic formatting\n ************************************************************/\n.section-navbar {\n\n\n	margin: 0 auto;\n	width: 100%;\n	font-family: @altFontFamily;\n	font-weight: @altFontWeight;\n\n	.content-pad,\n	.pl-section-pad{\n		padding-top: 5px;\n		padding-bottom: 5px;\n		.navbar-content-pad{\n			padding: 0 15px;\n		}\n	}\n\n}\n// Dont nest, needs to be minimum specificity\n.pldrop ul{\n	display: none;\n}\n.navbar{\n	.box-shadow(~"inset 0 1px 0 rgba(255,255,255,.1), 0 1px 3px rgba(0, 0, 0, 0.25)");\n	color:rgba(255,255,255,.7);\n	&.navbar-content-width{\n		.border-radius( 4px );\n	}\n	&.plnav_hover {\n		.sub-menu .dropdown-menu {\n			position: absolute;\n			top: 0;\n			left: 100%;\n			margin-top: -1px;\n			visibility: hidden;\n		}\n		.dropdown-menu li:hover .sub-menu {\n		  	visibility: visible;\n		  	margin-top:-6px;\n		}\n		.dropdown:hover .dropdown-menu {\n			margin-top:0;\n		  	display: block;\n		}\n	}\n	.navline {\n		float: left;\n		position: relative;\n		left: 0;\n		display: block;\n		margin: 0 10px 0 0;\n		font-size: .9em;\n\n		> .dropdown > .dropdown-menu{\n			&:before {\n			  content: '''';\n			  display: inline-block;\n			  border-left: 7px solid transparent;\n			  border-right: 7px solid transparent;\n			  border-bottom: 7px solid #ccc;\n			  border-bottom-color: rgba(0, 0, 0, 0.2);\n			  position: absolute;\n			  top: -7px;\n			  left: 9px;\n			}\n			&:after {\n			  content: '''';\n			  display: inline-block;\n			  border-left: 6px solid transparent;\n			  border-right: 6px solid transparent;\n			  border-bottom: 6px solid #ffffff;\n			  position: absolute;\n			  top: -6px;\n			  left: 10px;\n			}\n		}\n		.caret{\n			border-top-color:  rgba(255,255,255,.5);\n		}\n		&.pull-right {\n			float: right;\n			position: relative;\n			> li:last-child .dropdown-menu{\n				left: auto;\n				right: 0;\n				&:before{\n					left: auto;\n					right: 12px;\n				}\n				&:after{\n					left: auto;\n					right: 13px;\n				}\n				.dropdown-submenu > a:after {\n				  	border-width: 5px 5px 5px 0;\n					border-right-color: darken(@dropdownBackground, 20%);\n				}\n				.dropdown-menu {\n					left: -100%;\n					right: auto;\n					margin-left: 0;\n					margin-right: -1px;\n					.border-radius(6px 0 6px 6px);\n				}\n			}\n\n		}\n\n\n		> li {\n			display: block;\n			float: left;\n\n			> a {\n				text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);\n				&:hover{\n					color:rgba(255,255,255,.9);\n				}\n				float: none;\n				display: block;\n				padding: 9px 12px 9px;\n				line-height: 19px;\n				color:#fff; /* IE */\n				color:rgba(255,255,255,.8);\n				text-decoration: none;\n				text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);\n				&:hover{\n					background-color: transparent;\n					color:rgba(255,255,255,.9);\n					text-decoration: none;\n				}\n			}\n			&.dropdown.open > a{\n				color:#fff;\n				background: rgba(0, 0, 0, 0.1);\n			}\n			&.current-menu-item > a {\n			  	background-color: rgba(0,0,0,.1);\n				color:#ffffff;\n				text-decoration: none;\n			}\n			&.active > a:hover {\n				color:rgba(255,255,255,.9);\n				  text-decoration: none;\n				  background-color: #222222;\n			}\n		}\n	}\n\n	.searchform{\n		float: right;\n		margin: 7px 0 5px;\n		width: auto;\n		max-width: 190px;\n		.searchfield{\n			background-color:rgba(255,255,255,.35);\n\n			box-shadow: inset 0 1px 2px rgba(255, 255, 255, 0.15), 0 1px 0px rgba(255, 255, 255, 0.15);\n			&:focus{\n				box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.1), 0 1px 0px rgba(255, 255, 255, 0.15);\n				background-color:#fff;\n				color:#000;\n\n			}\n		}\n	}\n	.plbrand{\n		float:left;\n\n		margin: 4px 2% 0 0;\n		padding: 0 1%;\n		.plbrand-text{\n			color:#ffffff;\n			font-size: 1.4em;\n			line-height: 27px;\n		}\n		&:hover .plbrand-text{\n			color:#EEE;\n		}\n		&:active .plbrand-text{\n			color:#DDD;\n		}\n		img{\n			max-height: 29px;\n		}\n	}\n\n	&.pl-color-black-trans{\n		background: #000;\n		border-top: 1px solid rgba(0,0,0,.9);\n		.searchform .searchfield{\n			border: 1px solid rgba(0,0,0,.7);\n		}\n	}\n	&.pl-color-red{\n		#gradient > .vertical(@btnImportantBackground, @btnImportantBackgroundHighlight);\n		border-top: 1